document.querySelectorAll(".in-by-left").forEach(function(e){window.addEventListener("scroll",function(){window.innerHeight-e.getBoundingClientRect().top>0?(e.classList.add("fade-in-left"),e.classList.remove("fade-out-left")):(e.classList.remove("fade-in-left"),e.classList.add("fade-out-left"))})}),document.querySelectorAll(".in-by-right").forEach(function(e){window.addEventListener("scroll",function(){window.innerHeight-e.getBoundingClientRect().top>0?(e.classList.add("fade-in-right"),e.classList.remove("fade-out-right")):(e.classList.remove("fade-in-right"),e.classList.add("fade-out-right"))})}),document.querySelectorAll(".in-by-light-speed").forEach(function(e){window.addEventListener("scroll",function(){window.innerHeight-e.getBoundingClientRect().top>0?(e.classList.add("light-speed-in"),e.classList.remove("fade-out-right")):(e.classList.remove("light-speed-in"),e.classList.add("fade-out-right"))})});